-- Création de la table AdministrateurCREATE TABLE administrateur (    IDadministrateur INTEGER PRIMARY KEY AUTO_INCREMENT,    nom VARCHAR(50) NOT NULL ,    prenom VARCHAR(50) NOT NULL ,    identifiant VARCHAR(50) NOT NULL ,    mot_de_passe VARCHAR(250) NOT NULL ,    super_utilisateur BOOLEAN NOT NULL); -- Création de la table TypeCREATE TABLE type (    IDtype INTEGER PRIMARY KEY AUTO_INCREMENT,    nom VARCHAR(30) NOT NULL); -- Création de la table MultiplexeCREATE TABLE multiplexe (    IDmultiplexe INT PRIMARY KEY AUTO_INCREMENT,    nom VARCHAR(50));-- Création de la table RealisateurCREATE TABLE realisateur (    IDrealisateur INTEGER PRIMARY KEY AUTO_INCREMENT,    nom VARCHAR(50) NOT NULL,    prenom VARCHAR(50) NOT NULL,    date_de_naissance DATE NOT NULL,    en_vie BOOLEAN NOT NULL);-- Création de la table TarifCREATE TABLE tarif (    IDtarif INTEGER PRIMARY KEY AUTO_INCREMENT,    tarification VARCHAR(50) NOT NULL,    prix DECIMAL(5,2) NOT NULL);-- Création de la table ClientCREATE TABLE client (    IDclient INTEGER PRIMARY KEY AUTO_INCREMENT,    nom VARCHAR(50) NOT NULL,    prenom VARCHAR(50) NOT NULL,    email VARCHAR(200) NOT NULL,    telephone VARCHAR(20) NOT NULL);-- Création de la table PaiementCREATE TABLE paiement (    IDpaiement INT PRIMARY KEY AUTO_INCREMENT,    nom VARCHAR(30));-- Création de la table CinemaCREATE TABLE cinema (    IDcinema INTEGER PRIMARY KEY AUTO_INCREMENT,    adresse VARCHAR(250) NOT NULL,    telephone VARCHAR(20) NOT NULL,    email VARCHAR(255) NOT NULL,    ville VARCHAR(50) NOT NULL,    code_postal VARCHAR(5) NOT NULL,    nom VARCHAR(50) NOT NULL,    IDmultiplexe INTEGER NOT NULL,    IDadministrateur INTEGER NOT NULL,    FOREIGN KEY (IDmultiplexe) REFERENCES multiplexe (IDmultiplexe), -- Ajout de la contrainte de clé étrangère    FOREIGN KEY (IDadministrateur) REFERENCES administrateur (IDadministrateur) -- Ajout de la contrainte de clé étrangère);    -- Création de la table salleDeProjectionCREATE TABLE salleDeProjection (    IDsalleDeProjection INT PRIMARY KEY AUTO_INCREMENT,    numero VARCHAR(3) NOT NULL,    nb_place TINYINT NOT NULL,    nom VARCHAR(50) NOT NULL,    IDcinema INT NOT NULL,    FOREIGN KEY (IDcinema) REFERENCES cinema (IDcinema) -- Ajout de la contrainte de clé étrangère);-- Création de la table FilmCREATE TABLE film (    IDfilm INTEGER PRIMARY KEY AUTO_INCREMENT,    titre VARCHAR(50) NOT NULL,    duree TIME NOT NULL,    IDrealisateur INTEGER NOT NULL,    IDtype INTEGER NOT NULL,    FOREIGN KEY (IDrealisateur) REFERENCES realisateur (IDrealisateur), -- Ajout de la contrainte de clé étrangère    FOREIGN KEY (IDtype) REFERENCES type (IDtype) -- Ajout de la contrainte de clé étrangère);-- Création de la table SeanceCREATE TABLE seance (    IDseance INTEGER PRIMARY KEY AUTO_INCREMENT,    date DATE NOT NULL,    heure_debut TIME NOT NULL,    heure_fin TIME NOT NULL,    IDsalleDeProjection INTEGER NOT NULL,    IDadministrateur INTEGER NOT NULL,    IDfilm INTEGER NOT NULL,    FOREIGN KEY (IDsalleDeProjection) REFERENCES salleDeProjection (IDsalleDeProjection), -- Ajout de la contrainte de clé étrangère    FOREIGN KEY (IDadministrateur) REFERENCES administrateur (IDadministrateur), -- Ajout de la contrainte de clé étrangère    FOREIGN KEY (IDfilm) REFERENCES film (IDfilm) -- Ajout de la contrainte de clé étrangère);-- Création de la table ReservationCREATE TABLE reservation (    IDreservation INTEGER PRIMARY KEY AUTO_INCREMENT,    nb_place INTEGER NOT NULL,    reservationEnLigne BOOLEAN NULL,    IDclient INTEGER NOT NULL,    IDpaiement INTEGER NOT NULL,    IDtarif INTEGER NOT NULL,    IDseance INTEGER NOT NULL,    FOREIGN KEY (IDclient) REFERENCES client (IDclient), -- Ajout de la contrainte de clé étrangère    FOREIGN KEY (IDpaiement) REFERENCES paiement (IDpaiement), -- Ajout de la contrainte de clé étrangère    FOREIGN KEY (IDtarif) REFERENCES tarif (IDtarif), -- Ajout de la contrainte de clé étrangère    FOREIGN KEY (IDseance) REFERENCES seance (IDseance) -- Ajout de la contrainte de clé étrangère);-- Création de la table ligneDeReservationCREATE TABLE ligneDeReservation (    IDligneDeReservation INTEGER PRIMARY KEY AUTO_INCREMENT,    calcul_TVA DECIMAL(10,2) NOT NULL,    calcul_HT DECIMAL(10,2) NOT NULL,    calcul_TTC DECIMAL(10,2) NOT NULL,    IDreservation INTEGER NOT NULL,    FOREIGN KEY (IDreservation) REFERENCES reservation (IDreservation) -- Ajout de la contrainte de clé étrangère);  -- Contraintes d'intégritéALTER TABLE salleDeProjection ADD FOREIGN KEY (IDcinema) REFERENCES cinema (IDcinema);ALTER TABLE seance ADD FOREIGN KEY (IDsalleDeProjection) REFERENCES salleDeProjection (IDsalleDeProjection);ALTER TABLE seance ADD FOREIGN KEY (IDadministrateur) REFERENCES administrateur (IDadministrateur);ALTER TABLE seance ADD FOREIGN KEY (IDfilm) REFERENCES film (IDfilm);ALTER TABLE film ADD FOREIGN KEY (IDrealisateur) REFERENCES realisateur (IDrealisateur);ALTER TABLE film ADD FOREIGN KEY (IDtype) REFERENCES type (IDtype);ALTER TABLE reservation ADD FOREIGN KEY (IDclient) REFERENCES client (IDclient);ALTER TABLE reservation ADD FOREIGN KEY (IDpaiement) REFERENCES paiement (IDpaiement);ALTER TABLE reservation ADD FOREIGN KEY (IDtarif) REFERENCES tarif (IDtarif);ALTER TABLE reservation ADD FOREIGN KEY (IDseance) REFERENCES seance (IDseance);ALTER TABLE ligneDeReservation ADD FOREIGN KEY (IDreservation) REFERENCES reservation (IDreservation);ALTER TABLE cinema ADD FOREIGN KEY (IDmultiplexe) REFERENCES multiplexe (IDmultiplexe);ALTER TABLE cinema ADD FOREIGN KEY (IDadministrateur) REFERENCES administrateur (IDadministrateur);